package week1.day1;

public class NegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = -40;
		if (number < 0) {
			int number1 = number * (-1);
			System.out.println("The number " + -number + " is converted to " + number1);
		}
	}

}
